<script>
import gsap from "gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";
import { DrawSVGPlugin } from "gsap/dist/DrawSVGPlugin";
gsap.registerPlugin(DrawSVGPlugin, ScrollTrigger);
export default {
  methods: {
    badgeSVG() {
      const { fork, circle, letters, badge, bruja } = this.$refs;

      const tlRotate = new gsap.timeline({
        defaults: {
          repeat: -1,
          transformOrigin: "center",
          ease: "none",
        },
      });

      tlRotate
        .to(bruja, { duration: 30, rotation: "360" })
        .to(
          letters,
          {
            duration: 60,
            rotation: "360",
          },
          "0"
        )
        .to(
          fork,
          {
            drawSVG: "30% 30%",
            stroke: "white",
            strokeOpacity: 0.7,
            duration: 10,
            yoyo: true,
          },
          "0"
        );

      function badgeScroll(direction) {
        const scrollingDown = direction === 1;
        const tlBadge = gsap.timeline();

        tlBadge
          .to(badge, {
            scale: () => (scrollingDown ? 0.9 : 1),
          })
          .to(
            circle,
            {
              fillOpacity: () => (scrollingDown ? "0" : "1"),
            },
            "-=0.3"
          )
          .to(
            fork,
            {
              fillOpacity: () => (scrollingDown ? "0.9" : "1"),
              stroke: () => (scrollingDown ? "#13150e" : "#FFFFFF"),
            },
            "-=0.1"
          )
          .to(letters, { autoAlpha: () => (scrollingDown ? 0.8 : 1) }, "0");

        return tlBadge;
      }

      ScrollTrigger.create({
        start: "top+=100 2",
        end: "bottom bottom-=100",
        onEnter: ({ direction }) => {
          badgeScroll(direction);
        },
        onLeaveBack: ({ direction }) => {
          badgeScroll(direction);
        },
      });
    },
  },
  mounted() {
    this.badgeSVG();
  },
};
</script>

