<script>
import gsap from "gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";
import { DrawSVGPlugin } from "gsap/dist/DrawSVGPlugin";
gsap.registerPlugin(DrawSVGPlugin, ScrollTrigger);
export default {
  data: () => ({
    tlRotate: gsap.timeline({
      paused: true,
      defaults: {
        rotation: "360",
        repeat: -1,
        transformOrigin: "center",
        ease: "none",
      },
    }),
  }),
  methods: {
    badge() {
      const { letters, bruja, ring1, ring2, ring3 } = this.$refs;
      const tl = this.tlRotate;
      tl.to(bruja, { duration: 60 })
        .to(letters, { duration: 40 }, "0")
        .timeScale(0.5)
        .play();

      const tlBadgeScroll = new gsap.timeline();

      tlBadgeScroll
        .add("rings")
        .to(ring1, {
          y: "+=60",
          fillOpacity: 0.4,
        })
        .to(
          ring2,
          {
            y: "+=30",
            fillOpacity: 0.6,
          },
          "rings"
        )
        .to(
          [ring3, letters, bruja],
          {
            y: "+=20",
            fillOpacity: 0.8,
          },
          "rings"
        );

      ScrollTrigger.create({
        trigger: this.$parent.$refs.hero,
        animation: tlBadgeScroll,
        start: "center center-20%",
        end: "bottom top+=10%",
        scrub: 1
      });
    },
  },
  mounted() {
    this.badge();
  },
};
</script>

<style lang="postcss" scoped>
.cta__circle {
  @apply relative
    z-40
    w-9/12
    mx-auto
    sm:w-6/12
    lg:w-5/12;

  & a {
    @apply w-full;
  }

  & #rings {
    & path:first-child {
      @apply fill-gray-400 dark:fill-gray-300;
    }

    & path:nth-child(2) {
      @apply fill-[#193540];
    }
  }

  & #letters {
    @apply fill-slate800 dark:fill-white/75;
  }

  & #bruja_ring {
    & #bruja {
      @apply fill-slate800 dark:fill-gray-300;
    }
  }
}
</style>
