<script>
import gsap from 'gsap'
import { ScrollTrigger } from 'gsap/dist/ScrollTrigger'

export default {
  methods: {
    creatures: () => {
      gsap.registerPlugin(ScrollTrigger)
      const white = gsap.utils.toArray('#white path')
      const whiteRev = gsap.utils.toArray('#white path').reverse()

      const black = gsap.utils.toArray('#seres #black path')
      const blackRev = gsap.utils.toArray('#seres #black path').reverse()

      function seres(direction) {
        const scrollingDown = direction === 1

        const creaturesWhite = scrollingDown ? white : whiteRev

        const creaturesBlack = scrollingDown ? black : blackRev

        const creaturesTl = gsap.timeline({
          defaults: {
            stagger: 0.02,
            ease: 'back.out(3.4)',
          },
        })

        creaturesTl
          .to(creaturesWhite, {
            y: () => (scrollingDown ? '-=100' : 0),
          })
          .to(
            creaturesBlack,
            {
              y: () => (scrollingDown ? '-=150' : 0),
            },
            '-=1'
          )

        return creaturesTl
      }

      ScrollTrigger.create({
        start: 'top+=200 top',
        onEnter: ({ direction }) => {
          seres(direction)
        },
        onLeaveBack: ({ direction }) => {
          seres(direction)
        },
        // markers: true,
      })
    },
  },
  mounted() {
    this.creatures()
  },
}
</script>

