<script>
import gsap from "gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";
gsap.registerPlugin(ScrollTrigger);

export default {
  data: () => ({
    tlMasterUniverse: gsap.timeline({ paused: true }),
  }),
  methods: {
    universe() {
      const { GRP, corona, iris, sol, cardinales } = this.$refs;

      const tl = this.tlMasterUniverse;

      const tlScroll = new gsap.timeline({
        defaults: {
          transformOrigin: "center",
          repeat: -1,
          ease: "none",
        },
      });

      tlScroll
        .set(sol, { fill: "transparent" })
        .set(iris, { fill: "rgba(254,240,138,0.6)" })
        .to(corona, {
          fillOpacity: 0.6,
          rotation: 360,
        })
        .to(
          iris,
          {
            fillOpacity: 0.3,
            scale: 1.1,
          },
          "0"
        )
        .timeScale(0.02);

      tl.add("grid")
        .to(GRP, {
          yPercent: "+=10",
          scale: 0.9,
          strokeWidth: 3,
          strokeOpacity: 0.2,
          fill: "#FFFFFF20",
          fillOpacity: "0.1",
          transformOrigin: "center",
        })
        .to(
          cardinales,
          {
            yPercent: "+=10",
            autoAlpha: 0,
          },
          "grid"
        );

      ScrollTrigger.create({
        animation: tl,
        trigger: this.$parent.$refs.hero,
        start: "top top",
        scrub: 1,
      });
    },
  },
  mounted() {
    this.universe();
  },
};
</script>

